const WorkspaceError = require("./workspace-error.js");

const CreateAccessKey = {
  UC_CODE: `${WorkspaceError.APP_WORKSPACE_LIB_ERROR_PREFIX}logStoreService/createAccessKey/`,

  UuCloudLogStoreBaseUriNotConfigured: class extends WorkspaceError {
    static get code() { return `${CreateAccessKey.UC_CODE}uuCloudLogStoreBaseUriNotConfigured` }

    constructor() {
      super(...arguments);
      this.status = 500;
      this.message = 'The uuCloudLogStoreBaseUri parameter is not configured.';
      this.code = CreateAccessKey.UuCloudLogStoreBaseUriNotConfigured.code;
    }
  },

};


module.exports = {
  CreateAccessKey,
};
