const WorkspaceError = require("./workspace-error.js");

class AwidLicenseExceededError extends WorkspaceError {
  static get ERROR_CODE() {
    return `${WorkspaceError.APP_WORKSPACE_LIB_ERROR_PREFIX}awidLicenseExceeded`;
  }

  constructor(msg, paramMap) {
    super({}, paramMap);
    this.message = msg || "This UC can not be run with the current uuAppWorkspace license.";
    this.code = this.constructor.ERROR_CODE;
    this.status = 429;
  }
}

module.exports = {
  AwidLicenseExceededError,
};
