const WorkspaceError = require("./workspace-error.js");

class LicenseExceededError extends WorkspaceError {
  static get ERROR_CODE() {
    return `${WorkspaceError.APP_WORKSPACE_LIB_ERROR_PREFIX}licenseExceeded`;
  }

  constructor(msg, paramMap) {
    super({}, paramMap);
    this.message = msg || "This UC can not be run with the current license.";
    this.code = this.constructor.ERROR_CODE;
  }
}

module.exports = {
  LicenseExceededError
};
