"use strict";

const { UriBuilder } = require("uu_appg01_core-uri");

const ACCESS_KEY = "accessKey";
const ASID = "asid";
const AWID = "awid";
const GET_RECORD_LIST_UC = "log/getRecordList";

class LogStoreHelper {

	static createGetRecordListLink(accessKeyAttributes) {
		let builder = UriBuilder.parse(accessKeyAttributes.baseUri);
		builder.setUseCase(GET_RECORD_LIST_UC);
		builder.setParameter(ACCESS_KEY, accessKeyAttributes.accessKey);
		builder.setParameter(ASID, accessKeyAttributes.asid);
		if (accessKeyAttributes.awid) {
			builder.setParameter(AWID, accessKeyAttributes.awid);
		}
		return builder.toUri();
	}
}

module.exports = LogStoreHelper;