"use strict";
const SysQosError = require("./sys-qos-error");

const SysQosBadRequestError = {
  UC_CODE: `${SysQosError.ERROR_PREFIX}`,

  BadRequest: class extends SysQosError {
    constructor() {
      super(...arguments);
      this.status = 400;
      this.message = "Request URI is not valid.";
      this.code = `${SysQosBadRequestError.UC_CODE}badRequest`;
    }
  }
};

module.exports = { SysQosBadRequestError };
