"use strict";

const Environment = require("../environment.js");

module.exports = async function (environment, ...args) {
  let env = new Environment(environment, ...args);
  let projectPackageJson = env.getPackageConfig();
  let isLibWorkspace = projectPackageJson?.name?.includes("~root");
  if (isLibWorkspace) return require("./_app-box-lib.js")(environment, ...args);
  return require("./_app-box-app.js")(environment, ...args);
};
